import React, { Component } from 'react';


export default function Oval(props) {
    console.log(props)
    let textName = props.textName
    let header = '';

    if (props.header) {
        header = 'header';
    }

    let [styleType, setStyleType]=React.useState(props.styleType);

    let classes = {"ux": true, "dev": true, "fine-art": true, "visual": true}
    let enterHandler = () => {
        classes.styleType = false;
        setStyleType(props.styleType + '-hover')
        let ux = (document.body.getElementsByClassName(classes));
        ux = Array.prototype.slice.call( ux )
        ux.forEach(x => x.classList.add('grey'))
        console.log(styleType)
        classes.add(styleType)
    }
    let exitHandler = () => {
        setStyleType(props.styleType)
        console.log(styleType)
        console.log("leaving")
    }
    return (
        <div onMouseEnter={enterHandler} onMouseLeave={exitHandler} className={styleType + " oval " + header}>
            <h3>{textName}</h3>
        </div>

    )
}
