import { useState, useEffect } from "react";

const useMousePosition = () => {

if (document.getElementById('contact-email')) {
    const [position, setPosition] = useState({
        clientX: 0,
        clientY: 0,
      });

      const updatePosition = event => {
        const { pageX, pageY, clientX, clientY } = event;

        setPosition({
          clientX,
          clientY,
        });
      };

      useEffect(() => {
        document.getElementById('contact-email').addEventListener("mousemove", updatePosition, false);
        document.getElementById('contact-email').addEventListener("mouseenter", updatePosition, false);

        return () => {
            document.getElementById('contact-email').removeEventListener("mousemove", updatePosition);
            document.getElementById('contact-email').removeEventListener("mouseenter", updatePosition);
        };
      }, []);

      return position; 
}

};

export default useMousePosition;
