import React from 'react'

export default function WorkPageTextSection(props) {

    let header = props.header;
    let text = props.text;
    let textStyle = '';
    if (props.emphasis) {
        textStyle = '1.2em'
    }
    return (
        <div className="work-page-text-section">
            <h1 className="work-page-text-section-header">{header}</h1>
            <p style={{fontSize: textStyle}} className="work-page-text-section-text">{text}</p>
        </div>
    )
}
