import React from "react";
import useMousePosition from "./useMousePosition";
import CursorCopy from '../assets/cursor-copy.svg'
import { useState } from "react";
const Cursor = () => {
  const { clientX, clientY } = useMousePosition();
  const [state, setState] = useState(false);

  document.getElementById("contact-email").addEventListener("mouseenter", function() {setState(true);});
  document.getElementById("contact-email").addEventListener("mouseleave", function() {setState(false);});

  if (!state) {
    return
  }
  return (
    <div 
      style={{ 
        position: "fixed",
        top: 0,
        bottom: 0,
        left: 0,
        right: 0,
        zIndex: 9999,
        pointerEvents: "none"
      }}
    >
    <img width={50}
        height={50}
        viewBox="0 0 50 50"
        style={{
          position: "absolute",
          left: clientX,
          top: clientY,
          transform: "translate(-50%, -50%)",
        }} src={CursorCopy}></img>
    </div>
  );
};

export default Cursor;
