import React from 'react'
import { Redirect } from 'react-router'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
import projects from '../data/ux-data'
import WorkPageHeader from '../components/WorkPageHeader'
import MainLogo from '../assets/main-logo.svg'
import WorkPageSection from '../components/WorkPageSection';


export default function WorkPage({match: {params: { id }}, props}) {

    let thisProject = projects.filter((p) => p.id === '/' + p.category+ '/'+id);
    if (thisProject.length <= 0) {
        return (
            <Redirect to={{pathname: "/404"}}/>
        )
    } 


    let w = thisProject[0]

    return (
        <div>
            <Link to="/"><img className="logo"  src={MainLogo} alt='MainLogo' /></Link>
            <WorkPageHeader name={w.name} category={w.category} path={w.id} logotype={w.logotype} cover={w.cover} subcat={w.subcategory} tools={w.tools} textColor={w.textColor} mainColor={w.mainColor} accentColor={w.accentColor}/>
            {w.content.map(obj => {
                return <WorkPageSection header={obj.header} text={obj.text} />
            })}
        </div>
    )
}
